export function classNameOrEmptyArray(element) {
    const className = element.properties.className;
    if (Array.isArray(className) && className.every((el) => typeof el === 'string'))
        return className;
    return [];
}
export function getLanguage(node, aliases) {
    const className = classNameOrEmptyArray(node);
    for (const classListItem of className) {
        if (classListItem.startsWith('language-')) {
            const lang = classListItem.slice(9).toLowerCase();
            if (lang)
                return aliases[lang];
        }
    }
    return undefined;
}
