import { z } from 'zod';
export const heroSchema = z.object({
    prompt: z.string().optional(),
    examples: z
        .union([
        z.array(z.string()),
        z.array(z.object({
            icon: z.string().optional(),
            title: z.string(),
            prompt: z.string(),
        })),
    ])
        .optional(),
});
