import { z } from 'zod';
export const bannerSchema = z.object({
    content: z
        .string()
        .nonempty()
        .describe('The text content displayed in the banner. Supports basic MDX formatting including links, bold, and italic text. Custom components are not supported.'),
    dismissible: z
        .boolean()
        .optional()
        .describe('Whether to show the dismiss button on the right side of the banner'),
});
